CREATE TABLE `cackle_comment` (    `id` BIGINT(20) NOT NULL AUTO_INCREMENT,    `cackle_id` BIGINT(20) NULL DEFAULT NULL,    `parent_id` BIGINT(20) NULL DEFAULT NULL,    `channel` VARCHAR(2000) NULL DEFAULT NULL,    `site_id` BIGINT(20) NULL DEFAULT NULL,    `message` TEXT NULL,    `media` VARCHAR(1000) NULL DEFAULT NULL,    `status` TINYINT(1) UNSIGNED NULL DEFAULT NULL,    `rating` INT(11) NULL DEFAULT NULL,    `ip` VARCHAR(39) NULL DEFAULT NULL,    `author_id` BIGINT(20) NULL DEFAULT NULL,    `author_name` VARCHAR(60) NULL DEFAULT NULL,    `author_email` VARCHAR(100) NULL DEFAULT NULL,    `author_www` VARCHAR(200) NULL DEFAULT NULL,    `author_avatar` VARCHAR(200) NULL DEFAULT NULL,    `author_provider` VARCHAR(32) NULL DEFAULT NULL,    `created` DATETIME NULL DEFAULT NULL,    PRIMARY KEY (`id`));CREATE TABLE `cackle_common` (    `name` VARCHAR(50) NULL DEFAULT NULL,    `value` VARCHAR(50) NULL DEFAULT NULL,    PRIMARY KEY (`common_name`))